Login_js = function(runBeforeShow) {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobileimage_16': 'Login_mobileimage_16',
        'mobileimage_20': 'Login_mobileimage_20',
        'spacer_13': 'Login_spacer_13',
        'mobilelabel_10': 'Login_mobilelabel_10',
        'mobiletextinput_9': 'Login_mobiletextinput_9',
        'mobilelabel_11': 'Login_mobilelabel_11',
        'mobiletextinput_12': 'Login_mobiletextinput_12',
        'spacer_21': 'Login_spacer_21',
        'mobilebutton_14': 'Login_mobilebutton_14'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    if (navigator.userAgent.indexOf("IEMobile") != -1) {
        //Fix for jQuery Mobile's footer in Cordova webview on WP8 devices
        var msViewportStyle = document.createElement("style");
        msViewportStyle.appendChild(
        document.createTextNode("@media screen and (orientation: portrait){@-ms-viewport {width: 320px; height: 534px;user-zoom: fixed;zoom: 1;}}" + "@media screen and (orientation:landscape){@-ms-viewport{width:480px;user-zoom:fixed;zoom:1;}}"));
        document.getElementsByTagName("head")[0].appendChild(msViewportStyle);
    }

    Apperyio.CurrentScreen = 'Login';

    /*
     * Nonvisual components
     */
    var datasources = [];

    /*
     * Events and handlers
     */

    // Before Show
    var Login_beforeshow = function() {
            Apperyio.CurrentScreen = "Login";
            for (var idx = 0; idx < datasources.length; idx++) {
                datasources[idx].__setupDisplay();
            }
        };

    // On Load
    var Login_onLoad = function() {
            Login_elementsExtraJS();

            // TODO fire device events only if necessary (with JS logic)
            Login_deviceEvents();
            Login_windowEvents();
            Login_elementsEvents();
        };

    // screen window events
    var Login_windowEvents = function() {

            $('#Login').bind('pageshow orientationchange', function() {
                var _page = this;
                adjustContentHeightWithPadding(_page);
            });

        };

    // device events
    var Login_deviceEvents = function() {
            document.addEventListener("deviceready", function() {

            });
        };

    // screen elements extra js
    var Login_elementsExtraJS = function() {
            // screen (Login) extra code

        };

    // screen elements handler
    var Login_elementsEvents = function() {
            $(document).on("click", "a :input,a a,a fieldset label", function(event) {
                event.stopPropagation();
            });

            $(document).off("click", '#Login_mobilecontainer [name="mobilebutton_14"]').on({
                click: function() {
                    if (!$(this).attr('disabled')) {
                        Apperyio.navigateTo('Start_classsusp', {
                            transition: 'slide',
                            reverse: false
                        });

                    }
                },
            }, '#Login_mobilecontainer [name="mobilebutton_14"]');

        };

    $(document).off("pagebeforeshow", "#Login").on("pagebeforeshow", "#Login", function(event, ui) {
        Login_beforeshow();
    });

    if (runBeforeShow) {
        Login_beforeshow();
    } else {
        Login_onLoad();
    }
};

$(document).off("pagecreate", "#Login").on("pagecreate", "#Login", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    Login_js();
});