Settings_js = function(runBeforeShow) {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilelabel_3': 'Settings_mobilelabel_3',
        'mobilecheckboxgroup_4': 'Settings_mobilecheckboxgroup_4',
        'mobilecheckbox_5': 'Settings_mobilecheckbox_5',
        'mobilecheckbox_6': 'Settings_mobilecheckbox_6',
        'mobilecheckbox_7': 'Settings_mobilecheckbox_7',
        'spacer_25': 'Settings_spacer_25',
        'mobilelabel_24': 'Settings_mobilelabel_24',
        'mobilelabel_27': 'Settings_mobilelabel_27',
        'mobilelabel_30': 'Settings_mobilelabel_30',
        'mobilelabel_29': 'Settings_mobilelabel_29',
        'spacer_32': 'Settings_spacer_32',
        'mobilebutton_31': 'Settings_mobilebutton_31'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    if (navigator.userAgent.indexOf("IEMobile") != -1) {
        //Fix for jQuery Mobile's footer in Cordova webview on WP8 devices
        var msViewportStyle = document.createElement("style");
        msViewportStyle.appendChild(
        document.createTextNode("@media screen and (orientation: portrait){@-ms-viewport {width: 320px; height: 534px;user-zoom: fixed;zoom: 1;}}" + "@media screen and (orientation:landscape){@-ms-viewport{width:480px;user-zoom:fixed;zoom:1;}}"));
        document.getElementsByTagName("head")[0].appendChild(msViewportStyle);
    }

    Apperyio.CurrentScreen = 'Settings';

    /*
     * Nonvisual components
     */
    var datasources = [];

    /*
     * Events and handlers
     */

    // Before Show
    var Settings_beforeshow = function() {
            Apperyio.CurrentScreen = "Settings";
            for (var idx = 0; idx < datasources.length; idx++) {
                datasources[idx].__setupDisplay();
            }
        };

    // On Load
    var Settings_onLoad = function() {
            Settings_elementsExtraJS();

            // TODO fire device events only if necessary (with JS logic)
            Settings_deviceEvents();
            Settings_windowEvents();
            Settings_elementsEvents();
        };

    // screen window events
    var Settings_windowEvents = function() {

            $('#Settings').bind('pageshow orientationchange', function() {
                var _page = this;
                adjustContentHeightWithPadding(_page);
            });

        };

    // device events
    var Settings_deviceEvents = function() {
            document.addEventListener("deviceready", function() {

            });
        };

    // screen elements extra js
    var Settings_elementsExtraJS = function() {
            // screen (Settings) extra code

        };

    // screen elements handler
    var Settings_elementsEvents = function() {
            $(document).on("click", "a :input,a a,a fieldset label", function(event) {
                event.stopPropagation();
            });

            $(document).off("click", '#Settings_mobilecontainer [name="mobilebutton_31"]').on({
                click: function() {
                    if (!$(this).attr('disabled')) {
                        Apperyio.navigateTo('Login', {
                            transition: 'slide',
                            reverse: false
                        });

                    }
                },
            }, '#Settings_mobilecontainer [name="mobilebutton_31"]');

        };

    $(document).off("pagebeforeshow", "#Settings").on("pagebeforeshow", "#Settings", function(event, ui) {
        Settings_beforeshow();
    });

    if (runBeforeShow) {
        Settings_beforeshow();
    } else {
        Settings_onLoad();
    }
};

$(document).off("pagecreate", "#Settings").on("pagecreate", "#Settings", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    Settings_js();
});