Start_classsusp_js = function(runBeforeShow) {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilenavbar_94': 'Start_classsusp_mobilenavbar_94',
        'mobilenavbaritem_95': 'Start_classsusp_mobilenavbaritem_95',
        'mobilenavbaritem_96': 'Start_classsusp_mobilenavbaritem_96',
        'mobilenavbaritem_97': 'Start_classsusp_mobilenavbaritem_97',
        'mobilebutton_98': 'Start_classsusp_mobilebutton_98'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    if (navigator.userAgent.indexOf("IEMobile") != -1) {
        //Fix for jQuery Mobile's footer in Cordova webview on WP8 devices
        var msViewportStyle = document.createElement("style");
        msViewportStyle.appendChild(
        document.createTextNode("@media screen and (orientation: portrait){@-ms-viewport {width: 320px; height: 534px;user-zoom: fixed;zoom: 1;}}" + "@media screen and (orientation:landscape){@-ms-viewport{width:480px;user-zoom:fixed;zoom:1;}}"));
        document.getElementsByTagName("head")[0].appendChild(msViewportStyle);
    }

    Apperyio.CurrentScreen = 'Start_classsusp';

    /*
     * Nonvisual components
     */
    var datasources = [];

    /*
     * Events and handlers
     */

    // Before Show
    var Start_classsusp_beforeshow = function() {
            Apperyio.CurrentScreen = "Start_classsusp";
            for (var idx = 0; idx < datasources.length; idx++) {
                datasources[idx].__setupDisplay();
            }
        };

    // On Load
    var Start_classsusp_onLoad = function() {
            Start_classsusp_elementsExtraJS();

            // TODO fire device events only if necessary (with JS logic)
            Start_classsusp_deviceEvents();
            Start_classsusp_windowEvents();
            Start_classsusp_elementsEvents();
        };

    // screen window events
    var Start_classsusp_windowEvents = function() {

            $('#Start_classsusp').bind('pageshow orientationchange', function() {
                var _page = this;
                adjustContentHeightWithPadding(_page);
            });

        };

    // device events
    var Start_classsusp_deviceEvents = function() {
            document.addEventListener("deviceready", function() {

            });
        };

    // screen elements extra js
    var Start_classsusp_elementsExtraJS = function() {
            // screen (Start_classsusp) extra code

        };

    // screen elements handler
    var Start_classsusp_elementsEvents = function() {
            $(document).on("click", "a :input,a a,a fieldset label", function(event) {
                event.stopPropagation();
            });

            $(document).off("click", '#Start_classsusp_mobileheader [name="mobilebutton_98"]').on({
                click: function() {
                    if (!$(this).attr('disabled')) {
                        Apperyio.navigateTo('Settings', {
                            transition: 'slide',
                            reverse: false
                        });

                    }
                },
            }, '#Start_classsusp_mobileheader [name="mobilebutton_98"]');

        };

    $(document).off("pagebeforeshow", "#Start_classsusp").on("pagebeforeshow", "#Start_classsusp", function(event, ui) {
        Start_classsusp_beforeshow();
    });

    if (runBeforeShow) {
        Start_classsusp_beforeshow();
    } else {
        Start_classsusp_onLoad();
    }
};

